mapper.select:
select c.id,c.fullname,c.appellation,c.company,c.phone,c.mphone,c.source,u.name as owner,c.state from tbl_clue c join tbl_user u on c.owner = u.id


        c.fullname like '%' #{fullname} '%'


        and c.company like '%' #{company} '%'


        and c.phone like '%' #{phone} '%'


        and c.source =  #{source}


        and u.name = like '%' #{owner} '%'


        and c.mphone like '%' #{phone} '%'


        and c.state = #{state}



order by c.createTime desc limit #{skipCount},#{pageSize}

mapper.delete:
delete from tbl_clue
where id in

    #{id}
